USE ingestao_dados_trusted;

DROP TABLE IF EXISTS tb_relatorio;

CREATE TABLE tb_relatorio (
    nom_instituicao  VARCHAR(500),
    num_cnpj_instituicao  BIGINT PRIMARY KEY,
    nom_segto_instituicao VARCHAR(500),
    max_qtd_totl_clie_ccs_scr BIGINT,
    avg_ind_recl FLOAT,
    sum_qtd_totl_recl BIGINT,
    avg_ind_geral_instituicao FLOAT,
    avg_ind_remu_bene_instituicao FLOAT,
    dat_hor_psst DATETIME,
    anomesdia VARCHAR(10)
);


SELECT * FROM tb_relatorio;

DROP TABLE IF EXISTS tb_relatorio_trimestre;

CREATE TABLE tb_relatorio_trimestre(
    nom_instituicao  VARCHAR(500),
    num_cnpj_instituicao  BIGINT,
    nom_segto_instituicao VARCHAR(500),
    ano INT,
    num_trimestre INT,
    max_qtd_totl_clie_ccs_scr BIGINT,
    avg_ind_recl FLOAT,
    sum_qtd_totl_recl BIGINT,
    avg_ind_geral_instituicao FLOAT,
    avg_ind_remu_bene_instituicao FLOAT,
    dat_hor_psst DATETIME,
    anomesdia VARCHAR(10),
  
  	PRIMARY KEY (num_cnpj_instituicao, ano, num_trimestre)
);



SELECT * FROM tb_relatorio_trimestre;